bool PayCallback::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("payment_no"))
    {
        if(!validJsonOfField(1, "payment_no", pJson["payment_no"], err, true))
            return false;
    }
    else
    {
        err="The payment_no column cannot be null";
        return false;
    }
    if(pJson.isMember("raw_body"))
    {
        if(!validJsonOfField(2, "raw_body", pJson["raw_body"], err, true))
            return false;
    }
    else
    {
        err="The raw_body column cannot be null";
        return false;
    }
    if(pJson.isMember("signature"))
    {
        if(!validJsonOfField(3, "signature", pJson["signature"], err, true))
            return false;
    }
    if(pJson.isMember("serial_no"))
    {
        if(!validJsonOfField(4, "serial_no", pJson["serial_no"], err, true))
            return false;
    }
    if(pJson.isMember("verified"))
    {
        if(!validJsonOfField(5, "verified", pJson["verified"], err, true))
            return false;
    }
    if(pJson.isMember("processed"))
    {
        if(!validJsonOfField(6, "processed", pJson["processed"], err, true))
            return false;
    }
    if(pJson.isMember("received_at"))
    {
        if(!validJsonOfField(7, "received_at", pJson["received_at"], err, true))
            return false;
    }
    return true;
}
