bool PayLedger::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("user_id"))
    {
        if(!validJsonOfField(1, "user_id", pJson["user_id"], err, true))
            return false;
    }
    else
    {
        err="The user_id column cannot be null";
        return false;
    }
    if(pJson.isMember("order_no"))
    {
        if(!validJsonOfField(2, "order_no", pJson["order_no"], err, true))
            return false;
    }
    else
    {
        err="The order_no column cannot be null";
        return false;
    }
    if(pJson.isMember("payment_no"))
    {
        if(!validJsonOfField(3, "payment_no", pJson["payment_no"], err, true))
            return false;
    }
    if(pJson.isMember("entry_type"))
    {
        if(!validJsonOfField(4, "entry_type", pJson["entry_type"], err, true))
            return false;
    }
    else
    {
        err="The entry_type column cannot be null";
        return false;
    }
    if(pJson.isMember("amount"))
    {
        if(!validJsonOfField(5, "amount", pJson["amount"], err, true))
            return false;
    }
    else
    {
        err="The amount column cannot be null";
        return false;
    }
    if(pJson.isMember("balance"))
    {
        if(!validJsonOfField(6, "balance", pJson["balance"], err, true))
            return false;
    }
    if(pJson.isMember("created_at"))
    {
        if(!validJsonOfField(7, "created_at", pJson["created_at"], err, true))
            return false;
    }
    return true;
}
